import React from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
// Import the Kaaba icon
import { faKaaba } from '@fortawesome/free-solid-svg-icons';
export const Home2 = (props) => {
  return (
    <div style={{paddingTop:10}} id="features2" className="text-center">
      <div  className="container">

       <div className="row">
           {props.data
             ? props.data.map((d, i) => (
                 <div  key={`${d.title}-${i}`} style={{paddingLeft:120}}  className="col-xs-6 col-md-2">
                   {" "}
                   <FontAwesomeIcon   area-hidden='true' style={iconStyle} size='2x' icon={faKaaba} />
                   <h3 style={{width:'70%',display:'block',textAlign:'center',padding:'20px 0px',color:'#fff'}} >{d.title}</h3>
                   {// <p style={{display:'block',textAlign:'justify'}}>{d.text}</p>
                   }
                 </div>
               ))
             : "Loading..."}
          

            {// <div  style={{paddingLeft:100}}  className="col-xs-6 col-md-2">
            //     <FontAwesomeIcon icon={faKaaba} aria-hidden='true'  />
            //       <h3 style={{width:'70%',display:'block',textAlign:'center',padding:'20px 0px',color:'#fff'}}>Umarah</h3>
            // </div>
            }
        </div>
      </div>
    </div>

  );
};
export default Home2;


const iconStyle = {
  fontSize: '38px',
  transition: 'all 0.5s',
  color: '#fff',
  margin: '0',
  padding: '30px 0',
  width: '100px',
  height: '100px',
  borderRadius: '50%',
  background: 'linear-gradient(to right, #FFD700 0%, #FFA500 100%)',
  boxShadow: '10px 10px 10px rgba(0, 0, 0, 0.05)',
};
